import React from "react"

function LikeButton() {
  return (
    <button type="button" className="btn btn-primary">
      Like
    </button>
  )
}

type Props = {
  initialHeadline: string
  content: string
}
type State = {
  headline: string
  tag?: string
}

class NewsItem extends React.Component<Props, State> {
  constructor(props) {
    super(props)
    this.state = {
      headline: props.initialHeadline,
      tag: null,
    }
    this.onHeadlineInputChange = this.onHeadlineInputChange.bind(this)
    this.onTagSelectChange = this.onTagSelectChange.bind(this)
  }

  onHeadlineInputChange(event) {
    this.setState({ headline: event.target.value })
  }

  onTagSelectChange(event) {
    this.setState({ tag: event.target.value })
  }

  render() {
    const tagOptions = ["", "World", "Finance", "Sport", "Gossip"]
    return (
      <div className="media">
        <img
          alt="64x64"
          className="mr-3"
          data-src="holder.js/64x64"
          src="data:image/svg+xml;base64,"
          style={{
            width: 64,
            height: 64,
          }}
        ></img>
        <div className="media-body column p-3">
          <h4>
            {this.state.headline}{" "}
            <span className="badge badge-info">{this.state.tag}</span>
          </h4>
          <div className="mb-2">{this.props.content}</div>
          <div>
            <LikeButton />
          </div>
        </div>
        <div className="card column col-auto p-3">
          <div className="form-group">
            <input
              className="form-input"
              placeholder="Edit Headline"
              onChange={this.onHeadlineInputChange}
            />
          </div>
          <div className="form-group">
            <label className="form-label" htmlFor="tag-select">
              Select Tag
            </label>
            <select
              className="form-select ml-2"
              id="tag-select"
              onChange={this.onTagSelectChange}
            >
              {tagOptions.map((value) => (
                <option key={value}>{value}</option>
              ))}
            </select>
          </div>
        </div>
      </div>
    )
  }
}

export default function App() {
  return (
    <div className="container">
      <div className="row">
        <div className="col">
          <h1>News</h1>
        </div>
      </div>
      <div className="row mt-2">
        <div className="col">
          <NewsItem
            initialHeadline="News headline"
            content={`Cras sit amet nibh libero, in gravida nulla. Nulla vel metus scelerisque ante sollicitudin. Cras purus odio, vestibulum in vulputate at, tempus viverra turpis. Fusce condimentum nunc ac nisi vulputate fringilla. Donec lacinia congue felis in faucibus.`}
          />
        </div>
      </div>
    </div>
  )
}
